import React from 'react'
import "./BestSellingBrands.css"

const BestSellingBrands = () => {
 
  const brandsList = [
    { name: 'Apple', image: 'data:image/png;base64,' },
    { name: 'Dyson', image: 'data:image/png;base64,' },
    { name: 'Samsung', image: 'data:image/png;base64,' },
    { name: 'Nintendo', image: 'data:image/png;base64,' },
    { name: 'PlayStation', image: 'data:image/png;base64,' },
    { name: 'Lego', image: 'data:image/png;base64,' },
    { name: 'PokÃ©mon', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQRLWLzfaifNVgcZ2deThE8kd5ea-tg0_LOgg&s' }
  ];

  // Handle when images don't load properly
  const handleImageError = (event, brandName) => {
    event.target.style.display = 'none';
    event.target.parentElement.innerHTML = <span class="fallback-text">${brandName}</span>;
  };

  return (
    <section className="brands-section">
      <div className="container">
        <h2 className="section-heading">Best-selling brands on eBay</h2>
        
        <div className="brands-container">
          {brandsList.map((brand, index) => (
            <div key={index} className="brand-card">
              <div className="brand-circle">
                <img 
                  src={brand.image}
                  alt={`${brand.name} product`}
                  className="brand-image"
                  onError={(e) => handleImageError(e, brand.name)}
                />
              </div>
              <span className="brand-title">{brand.name}</span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default BestSellingBrands;